%angular

<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.5/leaflet.css" />
<h3>GeoWave Leaflet Map</h3>
<div id="map" style="height: 600px; width: 100%"></div>

<script type="text/javascript">
function initMap() {
    var map = L.map('map').setView([55.00, 5.00], 5);
    
    var basemaps = {
        OSM: L.tileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: 'Map data &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors',
            maxZoom: 15,
            minZoom: 2
        })
    };
    
    var overlays = {
        GDELT:L.tileLayer.wms('http://ec2-54-224-76-180.compute-1.amazonaws.com:8000/geoserver/geowave/wms?', {
            layers: 'geowave:gdeltevent',
            format: 'image/png',
            transparent: true
        }),
    
        Centroids:L.tileLayer.wms('http://ec2-54-224-76-180.compute-1.amazonaws.com:8000/geoserver/geowave/wms?', {
            layers: 'geowave:kmeans-centroids',
            format: 'image/png',
            transparent: true
        }),
    
        Hulls: L.tileLayer.wms('http://ec2-54-224-76-180.compute-1.amazonaws.com:8000/geoserver/geowave/wms?', {
            layers: 'geowave:kmeans-hulls',
            format: 'image/png',
            transparent: true
        })
    };

    L.control.layers(basemaps, overlays).addTo(map);
    
    basemaps.OSM.addTo(map);
}

// ensure we only load the script once, seems to cause issues otherwise
if (window.L) {
    initMap();
} else {
    console.log('Loading Leaflet library');
    var sc = document.createElement('script');
    sc.type = 'text/javascript';
    sc.src = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.5/leaflet.js';
    sc.onload = initMap;
    sc.onerror = function(err) { alert(err); }
    document.getElementsByTagName('head')[0].appendChild(sc);
}
</script>
